import sys
import functools
from typing import List

def less3(a, b):
  ar = []
  for i in range(5):
    ca = a[i%len(a)]
    cb = b[i%len(b)]
    if ca > cb:
      return 1
    if ca < cb:
      return -1
  
  return 0

# константин жмурко
def key(a):
  ar = []
  for i in range(5):
    ar.append(a[i % len(a)])
  return "".join(ar)

def solution(ar: List[str]) -> List[str]:
  ar.sort(reverse=True, key=functools.cmp_to_key(less3))
  #ar.sort(reverse=True, key=key)
  return ar

n = int(input())
ar = sys.stdin.readline().split()
print("".join(solution(ar)))

